import React from 'react';
import { useContext } from 'react';
import { VesselContext } from '../context';
import Title from '../components/Title';

// get all unique values
const getUnique = (items, value) => {
	return [ ...new Set(items.map((item) => item[value])) ];
};

export default function VesselFilter({ vessels }) {
	const context = useContext(VesselContext);
	const { handleChange, type, capacity, price, minPrice, maxPrice, minSize, maxSize, breakfast, pets } = context;

	// get unique types
	let types = getUnique(vessels, 'type');

	// add all
	types = [ 'all', ...types ];

	// map to jsx
	types = types.map((item, index) => {
		return (
			<option value={item} key={index}>
				{item}
			</option>
		);
	});

	let people = getUnique(vessels, 'capacity');
	people = people.map((item, index) => {
		return (
			<option key={index} value={item}>
				{item}
			</option>
		);
	});

	return (
		<section className="filter-container">
			<Title title="search vessels" />
			<form className="filter-form">
				{/* select type*/}
				<div className="form-group">
					<label htmlFor="type">vessel type</label>
					<select name="type" id="type" value={type} className="form-control" onChange={handleChange}>
						{types}
					</select>
				</div>
				{/* end select type*/}

				{/* capacity type*/}
				<div className="form-group">
					<label htmlFor="capacity">Capacity</label>
					<select
						name="capacity"
						id="capacity"
						value={capacity}
						className="form-control"
						onChange={handleChange}
					>
						{people}
					</select>
				</div>
				{/* end capacity type*/}

				{/* project price */}
				<div className="form-group">
					<label htmlFor="price">Length Ft</label>
					<input
						type="range"
						name="price"
						min={minPrice}
						max={maxPrice}
						id="price"
						value={price}
						onChange={handleChange}
						className="form-control"
					/>
				</div>
				{/* end of project price */}

				{/* size of vessel*/}
				{/* <div className="form-group">
					<label htmlFor="size">vessel size</label>
					<div className="size-inputs">
						<input
							type="number"
							name="minSize"
							id="size"
							value={minSize}
							onchange={handleChange}
							className="size-input"
						/>

						<input
							type="number"
							name="maxSize"
							id="size"
							value={maxSize}
							onchange={handleChange}
							className="size-input"
						/>
					</div>
				</div> */}
				{/* end of size */}

				{/* extras */}
				<div className="form-group">
					<div className="single-extra">
						<input
							type="checkbox"
							name="breakfast"
							id="breakfast"
							checked={breakfast}
							onChange={handleChange}
						/>
						<label htmlFor="breakfast">Vessels with MMSI</label>
					</div>
					<div className="single-extra">
						<input type="checkbox" name="pets" id="pets" checked={pets} onChange={handleChange} />
						<label htmlFor="pets">Vessels with IMO</label>
					</div>
				</div>
				{/* end of extras */}
			</form>
		</section>
	);
}
